var jsdom = require('jsdom');
var q = require('q');

var qfs = require('./qfs');

var charts = function(lang, period){

    var fetchDate = new Date()
    var metaSeperator = 'â€¢';

    var parseRepo = function($repo, position){

        var repo = {
            url: undefined,
            stars: undefined,
            position: (position + 1),
            lang: lang,
            period: period,
            date: fetchDate.toISOString()
        };

        var $url = $repo.find('h3.repo-list-name a');
        if(1 === $url.length){
            repo.url = $url.attr('href').trim();
        }

        var $meta = $repo.find('.repo-list-meta');

        if(1 === $meta.length){
            var meta = $meta.text().trim().split(metaSeperator);

            if(2 <  meta.length){
                var stars = parseInt(meta[1].trim(), 10);
                repo.stars = stars;
            }
        }

        return repo;
    };

    var parseRepos = function(window){

        var deferred = q.defer();

        var $ = window.$;

        var $repos = $('ol.repo-list li.repo-list-item');

        var repos = [];

        $repos.each(function(index){
            repos.push(
                parseRepo($(this), index)
            );
        });
        
        console.log(repos);
        deferred.resolve(repos);

        return deferred.promise;
        
    };

    var fetch = function(){
        var deferred = q.defer();

        jsdom.env(
            'https://github.com/trending/' + lang + '?since=' + period,
            ['http://code.jquery.com/jquery.js'],
            function (err, window) {
                if(err){
                    deferred.reject(err);
                }
                deferred.resolve(window);
            }
        );
        return deferred.promise;
    };

    var dir = function(dirname){

        var deferred = q.defer();

        qfs.fileExists(dirname)
        .then(function(){
            deferred.resolve();
        })
        .fail(function(){
            qfs.mkdir(dirname)
            .then(function(){
                deferred.resolve();      
            });
        })

        return deferred.promise;        

    };

    var saveRepos = function(repos){

        var deferred = q.defer();

        var dirname = './json/' + lang + '-daily'

        var filename = fetchDate.getFullYear() + '-' + ( fetchDate.getMonth() + 1) + '-' + fetchDate.getDate();

        dir(dirname)
        .then(function(){
            return qfs.writeFile(dirname + '/' + filename, JSON.stringify(repos));
        })
        .then(function(){
            deferred.resolve();      
        });

        return deferred.promise;    
    };

    fetch()
    .then(parseRepos)
    .then(saveRepos)
    .then(function(){
        console.log('done');
    })
    ;
    return;
    fetch()
    //.then(saveRepos)
    //.then(database.close)
    .then(function(){
        console.log('done');
    })

};

charts('javascript', 'daily');